include_once("workflows.php");
error_reporting(0);
$wf = new Workflows();
$date="{query}";
if(strcmp($date,"today") == 0) {
   $date=date('Y-m-d',time());
} else if(strcmp($date."yesterday") == 0) {
   $date=date('Y-m-d',time()-(60*60*24));
} else if(preg_match("/([0-9]+)\-([0-9]+)\-([0-9]+)/",$date,$match) == 0) {
   echo "Not a proper date. Try again!";
   die();
} else if(($match[1] < 2012) || ($match[2] > 12) || ($match[3] > 31)) {
   echo "Not a proper date. Try again!";
   die();
}
$file_handle = fopen($wf->data()."/projects.txt", "rb");
echo $date.":\n";
while (!feof($file_handle) ) {

   $proj = fgets($file_handle);
   $proj = substr($proj,0,-1);
   if ($proj != "") {
      $file=$wf->data()."/{$proj}_{$date}.txt";
      if(file_exists($file)) {
         $time=readDayTime($file);
         $stime = timeTostring($time);
         echo sprintf("%s: %s\n",$proj, $stime);
      } else {
         echo sprintf("%s: 00:00:00\n", $proj);
      }
   }
}

fclose($file_handle);

