$remp = "{query}";
$wf = new Workflows();
$proj = array();

$file_handle = fopen($wf->data()."/projects.txt", "rb");

while (!feof($file_handle) ) {

   $project = fgets($file_handle);
   if ($project != "") {
	  if(strcmp($project,$remp) != 0) {
		$proj[] = $project;
      }
	}
}

fclose($file_handle);

