dict.string:
DEVICENAME=$(networksetup -listallhardwareports | grep -Eo "iPhone.*$");
echo "<?xml version=\"1.0\"?>" echo "<items>"
if [[ $(networksetup -getnetworkserviceenabled "$DEVICENAME" | grep -c "Enabled") == 0 ]] then // Turn iPhone USB Tethering On echo "<item uid=\"iphone usb tethering enable\" arg=\"on\"><title>Turn $DEVICENAME Tethering On</title><subtitle></subtitle><icon>icon.png</icon></item>"
else
// IPv4 IPv4=$(networksetup -getinfo "$DEVICENAME" | grep "IP address" | egrep -o "([0-9]{1,3}\.){3,3}[0-9]{1,3}") if [[ ! -z "$IPv4" ]] then echo "<item uid=\"\" arg=\"$IPv4\"><title>$IPv4</title><subtitle>IPv4 address</subtitle><icon>icon.png</icon></item>" fi
// IPv6 IPv6=$(networksetup -getinfo "$DEVICENAME" | grep "IPv6 IP address") IPv6=${IPv6:17} if [[ "$IPv6" != "none" ]] then echo "<item uid=\"\" arg=\"$IPv6\"><title>$IPv6</title><subtitle>IPv6 address</subtitle><icon>icon.png</icon></item>" fi
// Turn iPhone USB Tethering Off echo "<item uid=\"\" arg=\"off\"><title>Turn $DEVICENAME Tethering Off</title><subtitle></subtitle><icon>icon.png</icon></item>"
fi
echo "</items>"
